import React, { useState } from 'react'
import Map from '../pages/map';

const LandingPage = () => {
  // 입력 폼 변화 감지하여 입력 값 관리
  const [Value, setValue] = useState("");
  // 제출한 검색어 관리
  const [Keyword, setKeyword] = useState("");

  // 입력 폼 변화 감지하여 입력 값을 state에 담아주는 함수
  const keywordChange = (e) => {
    e.preventDefault();
    setValue(e.target.value);
  }

// 제출한 검색어 state에 담아주는 함수
const submitKeyword = (e) => {
  e.preventDefault();
  setKeyword(Value);
}

// 검색어를 입력하지 않고 검색 버튼을 눌렀을 경우
const valueChecker = () => {
  if (Value === "") {
    alert ("검색어를 입력해주세요.")
  }
}

return (
  <div className="landing-page" style={{ width: "100px", height: "100px", color: 'red' }}>
    <div className="landing-page__inner" style={{ width: "100px", height: "100px", color: 'red' }}>
      <div className="search-form-container" style={{ width: "100px", height: "100px", color: 'red' }}>
        <form className="search-form" onSubmit={ submitKeyword } style={{
                border: 0,
                boxSizing: 'border-box',
                color: '#333',
                fontSize: '100%',
                font: 'inherit',
                margin: 0,
                padding: 0,
                textDecoration: 'none',
                verticalAlign: 'baseline',
        }}>
          <label htmlFor="place" className="form__label" style={{
              display: 'block',
              margin: '0 auto',
              maxWidth: '310px',
              position: 'relative',
              width: '100%',
          }}>
            <input type="text" id="movie-title" className="form__input" name="place" onChange={ keywordChange } placeholder="검색어를 입력해주세요. (ex: 강남 맛집)" style={{ width: "100px", height: "100px", color: 'red' }} required />
            <div className="btn-box" style={{ width: "100px", height: "100px", color: 'red' }}>
              <input style={{ width: "100px", height: "100px", color: 'red' }} className="btn form__submit" type="submit" value="검색" onClick={ valueChecker }/>
            </div>
          </label>
        </form>
      </div>
      {/* 제출한 검색어 넘기기 */}
      <Map searchKeyword={ Keyword }/>
    </div>
  </div>
)
}

export default LandingPage
