import React, { useCallback, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import {Button, Card, Popover, List, Comment, Avatar} from 'antd';
import {CommentOutlined, EllipsisOutlined, } from '@ant-design/icons';
import { useDispatch, useSelector } from 'react-redux';
import PostImages from './PostImages';
import CommentForm from './CommentForm';
import PostCardContent from './PostCardContent';
import { LOAD_POST_REQUEST, REMOVE_POST_REQUEST } from '../reducers/post';
import FollowButton from './FollowButton';
// import LikeButton from './LikeButton';
import { LOAD_USER_REQUEST } from '../reducers/user';

const PostCard = ({post}) => {
    // const id = user?.id -> user && user.id; 옵셔널 체이닝 연산자
    const id = useSelector((state) => state.user.user?.id);
    // 위의 두 줄을 이렇게 합쳐도 가능
    const dispatch = useDispatch();
    const [commentOpend, setCommentOpend] = useState(false);
    const onToggleComment = useCallback(() => {
        setCommentOpend((prev) => !prev);
    })

    useEffect(() => {
      dispatch({
          type: LOAD_USER_REQUEST,
      });
      dispatch({
          type: LOAD_POST_REQUEST,
      });
      }, []);


    const onRemovePost = useCallback(() => {
        dispatch({
            type: REMOVE_POST_REQUEST,
            data: post.id,
        },[]);
    })

    return (
        <div>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <CommentOutlined key="comment" onClick={onToggleComment}/>,
                    <LikeButton key="like" post={post}/>,
                    <Popover key="more" content={(
                        <Button.Group>
                            {id && post.User?.id === id ? (
                            <>
                                <Button>수정</Button>
                                <Button type='danger' onClick={onRemovePost}>삭제</Button>
                            </>
                            ) : <Button>신고</Button> }
                        </Button.Group>
                    )}>
                    <EllipsisOutlined />
                    </Popover>
                ]}    
                extra={id && <FollowButton post={post} />}
                >
                <Card.Meta 
                    avatar={<Avatar>{post.User?.nickname[0]}</Avatar>}
                    title={post.User?.nickname}
                    description={<PostCardContent postData={post.content} />}
                />
            </Card>
            {commentOpend && (
                <div>
                    <CommentForm post={post}/>
                    <List
                        header={`${post.Comment.length}개의 댓글`}
                        itemLayout='horizontal'
                        dataSource={post.Comment}
                        renderItem={(item) => (
                            <li>
                                <Comment
                                    author={item.User.nickname}
                                    avatar={<Avatar>{item.User.nickname[0]}</Avatar>}
                                    content={item.content}
                                />
                            </li>
                        )}
                    />
                </div> )}
        </div>
    );
};

PostCard.propTypes = {
    post: PropTypes.shape({ //shape를 통해 더 구체적으로 작성이 가능하다
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.object,
        Comment: PropTypes.arrayOf(PropTypes.object), // 객체들의 배열
        Images: PropTypes.arrayOf(PropTypes.object),
    }).isRequired,
}

export default PostCard;
