import React, { useState } from 'react'
import Map from '../components/Map';
import axios from 'axios';
import { END } from 'redux-saga';
import { LOAD_MY_INFO_REQUEST, SIGN_UP_REQUEST } from '../reducers/user';
import wrapper from '../store/configureStore';

const LandingPage = () => {
  // 입력 폼 변화 감지하여 입력 값 관리
  const [Value, setValue] = useState("");
  // 제출한 검색어 관리
  const [Keyword, setKeyword] = useState("");

  // 입력 폼 변화 감지하여 입력 값을 state에 담아주는 함수
  const keywordChange = (e) => {
    e.preventDefault();
    setValue(e.target.value);
  }

// 제출한 검색어 state에 담아주는 함수
const submitKeyword = (e) => {
  e.preventDefault();
  setKeyword(Value);
}

// 검색어를 입력하지 않고 검색 버튼을 눌렀을 경우
const valueChecker = () => {
  if (Value === "") {
    alert ("검색어를 입력해주세요.")
  }
}

return (
  <div className="landing-page" style={{
    border: 0,
    boxSizing: 'border-box',
    color: '#333',
    fontSize: '100%',
    font: 'inherit',
    margin: 0,
    padding: 0,
    textDecoration: 'none',
    verticalAlign: 'baseline',
    height: '100%',
    display: 'block',
  }}>
    <div className="landing-page__inner" style={{ position: 'relative' }}>
      <div className="search-form-container" style={{ 
              position: 'relative',
              left: 0,
              padding: '0.75rem 0',
              position: 'absolute',
              top: 0,
              width: '100%',
              zIndex: 99,
              border: 0,
              boxSizing: 'border-box',
              color: '#333',
              margin: 0,
              padding: 0,
              textDecoration: 'none',
              verticalAlign: 'baseline',
              display: 'block',
       }}>
        <form className="search-form" onSubmit={ submitKeyword } style={{
                border: 0,
                boxSizing: 'border-box',
                color: '#333',
                fontSize: '100%',
                font: 'inherit',
                margin: 0,
                padding: 0,
                textDecoration: 'none',
                verticalAlign: 'baseline',
                display: 'block',
                position: 'absolute',
                top: '20vh',
                left: '5vw'

        }}>
          <label htmlFor="place" className="form__label" style={{
              display: 'block',
              margin: '0 auto',
              maxWidth: '310px',
              position: 'relative',
              width: '100%',
          }}>
            <input type="text" id="movie-title" className="form__input" name="place" onChange={ keywordChange } placeholder="검색어를 입력해주세요. (ex: 애견카페)" style={{ 
                borderRadius: '3px',
                boxShadow: '1px 1px 1px 1px rgb(0 0 0 / 20%)',
                display: 'block',
                fontSize: '16px',
                height: '45px',
                lineHeight: '45px',
                padding: '0 15px 0 35px',
                width: '100%',
                border: '1px solid #bbb',
             }} required />
            <div className="btn-box" style={{ 
                height: '45px',
                left: 0,
                position: 'absolute',
                top: 0,
                width: '33px', }}>
              <input style={{ 
                  backgroundSize: '20px auto',
                  border: 'none',
                  color: 'transparent',
                  fontSize: 0,
                  height: '100%',
                  lineHeight: 1,
                  textIndent: '-9999px',
                  width: '100%',
              }} className="btn form__submit" type="submit" value="검색" onClick={ valueChecker }/>
            </div>
          </label>
        </form>
      </div>
      {/* 제출한 검색어 넘기기 */}
      <Map searchKeyword={ Keyword } style={{position: 'relative'}}/>
    </div>
  </div>
)
}


export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
  console.log('getServerSideProps start');
  console.log(context.req.headers);
  const cookie = context.req ? context.req.headers.cookie : '';
  axios.defaults.headers.Cookie = '';
  if (context.req && cookie) {
    axios.defaults.headers.Cookie = cookie;
  }
  context.store.dispatch({
    type: LOAD_MY_INFO_REQUEST,
  });
  context.store.dispatch(END);
  console.log('getServerSideProps end');
  await context.store.sagaTask.toPromise();
});

export default LandingPage
